  constructor(){
    super() ;
    this.state = {
      articles : [] ,
      loading : false ,
      page : 1
    }
  }

  async componentDidMount()
  {
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=8fb2201cb717476999f7ae3805c5efb0&page=1" ;
    let data = await fetch(url) ;
    let parsedData = await data.json() ;
    this.setState({articles : parsedData.articles}) ;
    // console.log(parsedData) ;
  }

  prevClick = async() =>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=8fb2201cb717476999f7ae3805c5efb0&page=${this.state.page - 1}&pageSize=15` ;
    let data = await fetch(url) ;
    let parsedData = await data.json() ;
    this.setState({
      articles : parsedData.articles ,
      page : this.state.page - 1 
    }) ;
  }

  clickNext = async() => {

    if(this.state.page + 1 > Math.ceil(this.state.totalResults/15)){
      <p>
        {`: (`}
      <a href="">Go to previous page</a>
      </p>
    }

    else{
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=8fb2201cb717476999f7ae3805c5efb0&page=${this.state.page + 1}&pageSize=15` ;
    let data = await fetch(url) ;
    let parsedData = await data.json() ;
    this.setState({
      articles : parsedData.articles ,
      page : this.state.page + 1 
    }) ;
  }
  }

  render() {
    return (
      <div className="container my-3">
        <div className="mb-4">
            <h2>News of the day</h2>
        </div>
        <div className="row">
          {this.state.articles.map((element) => {

              return <div className="col-md-4" key = {element.url}>
              <NewsItem title={element.title ? element.title.slice(0 , 40) : ""} description={element.description ? element.description.slice(0 , 80) : ""} imgUrl = {element.urlToImage ? element.urlToImage : "https://resize.indiatvnews.com/en/resize/newbucket/1200_-/2019/11/whatsapp-image-2019-11-24-at-16-1574593427.jpeg"} newsUrl = {element.url}/>
          </div>

          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled = {this.state.page <= 1}className='btn btn-outline-warning' onClick={this.prevClick}> &larr; Prev</button>
          <button className='btn btn-outline-warning' onClick={this.clickNext}>Next &rarr;</button>
        </div>
      </div>
    )
  }
}

export default News
