import React, { Component } from 'react'
import NewsItem from './NewsItem'
// import PropTypes from 'prop-types'

export class News extends Component {

  constructor() {
    super();
    this.state = {
      articles: [],
        page : 1,
          loading : false
    }
  }

  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=8fb2201cb717476999f7ae3805c5efb0" ;
    let data = await fetch(url) ;
    let parsedData = await data.json() ;
    this.setState({articles : parsedData.articles , totalResults : parsedData.totalResults})
  }

  render() {
    return (
      <div className="container my-3">
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
              <NewsItem title={element.title ? element.title.slice(0 , 45) : ""} description={element.description ? element.description.slice(0 , 80) : ""} imgUrl={element.urlToImage} newsUrl={element.url} />
            </div>
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
          <button type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
      </div>
    )
  }
}

export default News
